    // pts,
    // reb,
    // ast,
    // stl,
    // blk,
    // tov
    useEffect(() => {
        axios.get("http://localhost:8080/player")
        .then((data) => {
            setPlayerData(data)
            setSeasonStats(data.data.seasonTotalsRegularSeason)
            // setSeasonStats(data.data.seasonTotalsRegularSeason.filter((item) =>
            //     item.seasonId === "2020-21" && 
