import React, { useRef, useState, useEffect } from 'react'
import * as d3 from 'd3'


var data = {
    "name": "A1",
    "children": [
      {
        "name": "B1",
        "children": [
          {
            "name": "C1",
            "value": 100
          },
          {
            "name": "C2",
            "value": 300
          },
          {
            "name": "C3",
            "value": 200
          }
        ]
      },
      {
        "name": "B2",
        "value": 200
      }
    ]
  };

function CirclePacking() {
    const [data, setData] = useState([25, 36, 47, 58])
    const svgRef = useRef()

    useEffect(() => {
        const svg = d3.select(svgRef.current)
        var diameter = 300
        var packLayout = d3.pack()
            .size([diameter, diameter])
            .padding(1.5);

        var rootNode = d3.hierarchy(data)

        rootNode.sum(function(d) {
            return d.value;
        });   

        // const myLine = d3.line()
        //     .x((value, index) => index * 50)
        //     .y(value => 150 - value)

        svg.attr("width", diameter)
            .attr("height", diameter)
            .attr("class", "packingLayout")

         packLayout(rootNode);

        svg.selectAll('circle').join('g')
            .data(rootNode.descendants())
            .enter()
            .append('circle')
            .attr('cx', d => d.x)
            .attr('cy', d => d.y)
            .attr('r', d => d.r)
            .attr('fill', 'red')
        // svg.selectAll("path")
        //     .data([data])
        //     .join("path")
        //     .attr("d", value => myLine(value))
        //     .attr("fill", "none")
        //     .attr("stroke", "red")

    },[])

    return (
        <svg ref = {svgRef}> </svg>
    )
}

export default CirclePacking
