import React, {Component} from 'react';

import RadarChart from 'react-vis/radar-chart';
import CircularGridLines from 'react-vis/plot/circular-grid-lines';

const DOMAIN = [
  {name: 'Field-Goal-%', domain: [0, 1], tickFormat: t => t},
  {name: 'Free-Throw-%', domain: [0, 1]},
  {name: '3s-%', domain: [0, 1]},
  {name: 'Points', domain: [0, 35], tickFormat: t => t},
  {name: 'Minutes', domain: [0, 40]},
  {name: 'Rebounds', domain: [0, 25]},
  {name: 'Assists', domain: [0, 20]},
  {name: 'Blocks', domain: [0, 10]},
  {name: 'Turnovers', domain: [0, 10]}
];


function RadarStats({input}) {
  const [data, setData] = (input.data)

    return (
      <div className="centered-and-flexed">
        <RadarChart
          animation
          data={data}
          domains={DOMAIN}
          style={{
            polygons: {
              fillOpacity: 0,
              strokeWidth: 3
            },
            axes: {
              text: {
                opacity: 1
              }
            },
            labels: {
              textAnchor: 'middle'
            }
          }}
          margin={{
            left: 30,
            top: 30,
            bottom: 40,
            right: 50
          }}
          tickFormat={t => ''}
          width={400}
          height={300}
        >
          <CircularGridLines
            tickValues={[...new Array(10)].map((v, i) => i / 10 - 1)}
          />
        </RadarChart>
      </div>
    );
  }

  export default RadarStats
