import React from 'react'


function Post({post}) {
    const style = {
        backgroundImage: `url("${require(`../../Assets/Images${post.image}`)}")`
    }
    return (
        <a className = "masonry-post" style = {style} href = {post.path}>{post.heading}</a>
    )
}
export default Post
